INSERT INTO `e2BlogNotes` (`SubsetID`, `Title`, `Text`, `FormatterID`, `OriginalAlias`, `Uploads`, `IsPublished`, `Stamp`, `LastModified`, `Offset`, `IsDST`) VALUES (1, 'Attio Adds Meeting Overview to Quick Actions', '((https://www.attio.com/?r=-7hFRgCS6dZ2pDVW/ Attio)) just made managing meetings simpler with a new update to Quick actions. Now, when you open Quick actions and select “View upcoming meetings,” you’ll see a streamlined view of all your meetings for today and tomorrow — all in one place.\r\n\r\nThe view includes:\r\n- Meeting times and calendar info\r\n- Recording status and controls\r\n- Participant lists and attendance tracking\r\n\r\nYou can quickly review your day, confirm who’s joining, and toggle recordings on or off ahead of time — helpful for teams that record selectively for privacy or documentation.\r\n\r\nThis makes it easier to prepare before calls, avoid missing key recordings, and stay in control without jumping between tools.', 'neasden', 'attio-adds-meeting-overview-to-quick-actions', '', '0', '1747827676', '1747827676', '18000', '0');
